function init() {

	var gr = document.getElementById('main');

	system = flaskfiller({
	  	x: 5,
		y: 5,
		flask: {
			type: 'bowl',
			top: 160,
			bottom: 30,
			max: 200,
			height: 100,
			width: 160,
			top_skip: 5,
			foot: false
		},
		configuration: {
		  measurelines: true,
		  graph: true,
		  time: true,
		  water_speed: 100
		}
	});

	gr.appendChild( system.element );
};
