svg.script:
function init() {

	var gr = document.getElementById('main');

	system = flaskfiller({
	  	x: 5,
		y: 5,
		flask: {
			type: 'default',
			top: 70,
			bottom: 50,
			max: 200,
			height: 90,
			top_skip: 8,	
			foot: false
		},
		configuration: {
		  measurelines: true,
		  graph: true,
		  time: true,
		  water_speed: 10
		}
	});

	gr.appendChild( system.element );
};
